        $sampleData->is_active = false;
        $sampleData->element_properties = $this->sampleSmkElements;
        $sampleData->save();

        $newData = new SmkElement();
        $newData->title = 'smk_elements_2';
        $newData->element_properties = $this->smkElementsProd;
        $newData->save();
    }
}
